package week1.day2;

public class ConvertedNegativeToPostive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num =-60;

		if (num<0) {
			System.out.println("Given num is negative Number :"+num);
		}
		else {
			System.out.println("Given num is Postive number");

		}
		num=num*(-1);
		System.out.println("Given number converted to negative to postive "+num);
		
		



	

	}

}
