package week1.day2;

import java.util.Iterator;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num =30;
		 for (int i = 0; i <=30; i++) {
			 if (i%2!=0) {
				 
				 if (i==27) {
					 break;
					
				}
				 System.out.println("odd number: "+i);
				
			}
			 
			 
			 
		 }
			 
	for (int j = 0; j <=num; j++) {
		
		if (j%2==0) {
			
			if (j==16) {
				continue;
				
			}
			System.out.println("even number: "+j);
			
		}
		
	}		 
			 
			 
			 
			 
			 
			 
			 
			 
			 
			 
			 
			 
			 
			 
			
		}

	}


